mapper.select:
select seq, title, writer, contents, to_char(wdate, 'yyyy-mm-dd') wdate, user_name, hit, num, image1 from ( select seq , title , writer , contents , wdate , hit , user_name , image1 , row_number() over(order by seq desc) num , ceil(row_number() over(order by seq desc)/#{pgSize})-1 pg from tb_Gallery A left outer join tb_member B on A.writer=B.user_id
where title like '%'||#{searchKeyword}||'%'
where contents like '%'||#{searchKeyword}||'%'
where title like '%'||#{searchKeyword}||'%' or contents like '%'||#{searchKeyword}||'%'
) A where A.pg=#{pg}

mapper.insert:

select nvl(max(seq), 0)+1 as seq
from tb_Gallery

insert into tb_Gallery
(
	seq
	, title
	, writer
	, contents
	, wdate
	, hit
	, image
)
values
(
	#{seq}
	, #{title}
	, #{writer}
	, #{contents}
	, sysdate
	, 0
	,#{image1}
)
